    def get_targets(self, mode, system, radmax):
        """Gets the specific set of targets based on config parameters"""

        targets = self.targets
        sub_targets = {}

        if mode == "single":
            for key in targets.keys():
                if key.find(system) != -1:
                    sub_targets[key] = targets[key]
                    break

            return sub_targets

        radmax = int(radmax)
        radmax_keys = []

        for key in targets.keys():
            if np.all(np.array([targets[key][j][1] for j in range(1, len(targets[key]))]) < radmax):
                radmax_keys.append(key)

        for key in radmax_keys:
            if mode == "TESS" and key.find("TOI") != -1 and key.find("test") == -1:
                sub_targets[key] = targets[key]

            elif mode == "Kepler" and (key.find("Kepler") != -1 or key.find("K2") != -1) and key.find("test") == -1:
                sub_targets[key] = targets[key]

            elif mode == "all" and key.find("test") == -1:
                sub_targets[key] = targets[key]

            elif mode == "test" and key.find("test 3") != -1:
                sub_targets[key] = targets[key]

        return sub_targets
